mapper.update:
UPDATE QNA
   SET QNA_CONTENT = #{qnaContent}
 WHERE QNA_NO = #{qnaNo}

mapper.select:
SELECT QNA_NO, MEMBER_ID, QNA_TITLE, QNA_CONTENT, QNA_CREATED, QNA_MODIFIED
  FROM QNA
 WHERE QNA_NO = #{qnaNo}

mapper.update:
UPDATE QNA_REPLY
   SET QNA_REPLY_GROUP_ORD = QNA_REPLY_GROUP_ORD + 1
 WHERE QNA_REPLY_GROUP_NO = #{qnaReplyGroupNo}
   AND QNA_REPLY_GROUP_ORD > #{qnaReplyGroupOrd}

UPDATE QNA_REPLY
   SET QNA_REPLY_STATE = -1
 WHERE QNA_REPLY_NO = #{qnaReplyNo}
