mapper.select:
SELECT RE_IMAGE_NO, REVIEW_NO, RE_IMAGE_PATH, RE_IMAGE_SAVED, RE_IMAGE_ORIGIN
  FROM RE_IMAGE
 WHERE REVIEW_NO = #{reviewNo}

SELECT REVIEW_NO, MEMBER_ID, RESER_NO, REVIEW_TITLE, REVIEW_CONTENT, REVIEW_CREATED,REVIEW_MODIFIED, REVIEW_REV_NO
  FROM REVIEW
 WHERE REVIEW_NO = #{reviewNo}

SELECT RE_IMAGE_NO, REVIEW_NO, RE_IMAGE_PATH, RE_IMAGE_SAVED, RE_IMAGE_ORIGIN
  FROM RE_IMAGE
 WHERE RE_IMAGE_NO = #{reImageNo}

mapper.update:
UPDATE REVIEW
   SET REVIEW_TITLE = #{reviewTitle}, REVIEW_CONTENT = #{reviewContent} , REVIEW_MODIFIED = SYSDATE ,REVIEW_REV_NO=#{reviewRevNo}
 WHERE REVIEW_NO = #{reviewNo}
